import React, { FormEvent, useEffect, useRef, useState } from "react";
import "./App.css";

type MessageRole = "teacher" | "student" | "system";

type Message = {
  id: string;
  role: MessageRole;
  text: string;
  createdAt: string;
};

const API_BASE = "http://127.0.0.1:8000";

const createMessage = (role: MessageRole, text: string): Message => ({
  id: `${Date.now()}-${Math.random().toString(16).slice(2)}`,
  role,
  text,
  createdAt: new Date().toISOString(),
});

const formatTime = (iso: string) =>
  new Date(iso).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

const ROLE_META: Record<MessageRole, { label: string; initial: string }> = {
  teacher: { label: "Teacher", initial: "T" },
  student: { label: "Student", initial: "S" },
  system: { label: "System", initial: "â„¹" },
};

export default function ChatApp() {
  const [messages, setMessages] = useState<Message[]>(() => [
    createMessage(
      "system",
      "Welcome! Start the lesson to see the student's recall evolve. Use /search <keywords> to review stored memories."
    ),
  ]);
  const [input, setInput] = useState("");
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [isSending, setIsSending] = useState(false);

  const bottomRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isSending]);

  const processInput = async (rawInput: string) => {
    const trimmed = rawInput.trim();
    if (!trimmed) return;

    const isCommand = trimmed.startsWith("/");
    if (isSending && !isCommand) return;

    setInput("");

    if (isCommand) {
      setMessages((prev) => [...prev, createMessage("teacher", trimmed)]);
      await handleCommand(trimmed);
      return;
    }

    await sendPrompt(trimmed);
  };

  const handleSubmit = async (event: FormEvent) => {
    event.preventDefault();
    await processInput(input);
  };

  const sendPrompt = async (prompt: string) => {
    const teacherMessage = createMessage("teacher", prompt);
    setMessages((prev) => [...prev, teacherMessage]);
    setIsSending(true);

    try {
      const url = new URL(`${API_BASE}/chat`);
      url.searchParams.set("prompt", prompt);
      if (sessionId) {
        url.searchParams.set("session_id", sessionId);
      }

      const response = await fetch(url.toString(), { method: "POST" });
      if (!response.ok) {
        const errorText = await response.text();
        setMessages((prev) => [
          ...prev,
          createMessage("system", `âŒ Server error: ${errorText || response.statusText}`),
        ]);
        return;
      }

      const data = await response.json();
      setSessionId(data.session_id ?? sessionId);

      const assistantReply =
        typeof data.response === "string" && data.response.trim().length > 0
          ? data.response.trim()
          : "I did not receive a response from the model.";

      setMessages((prev) => [...prev, createMessage("student", assistantReply)]);
    } catch (error) {
      console.error(error);
      setMessages((prev) => [
        ...prev,
        createMessage("system", "âš ï¸ Connection error. Confirm the FastAPI server is running."),
      ]);
    } finally {
      setIsSending(false);
    }
  };

  const handleCommand = async (rawCommand: string) => {
    const [commandToken, ...args] = rawCommand.slice(1).trim().split(/\s+/);
    if (!commandToken) {
      return;
    }

    const normalized = commandToken.toLowerCase();

    if (normalized === "search") {
      const query = args.join(" ").trim();
      if (!query) {
        setMessages((prev) => [
          ...prev,
          createMessage("system", "Usage: /search <keywords>"),
        ]);
        return;
      }
      try {
        const url = new URL(`${API_BASE}/search`);
        url.searchParams.set("query", query);
        const res = await fetch(url.toString());
        if (!res.ok) {
          const text = await res.text();
          setMessages((prev) => [
            ...prev,
            createMessage("system", `âŒ Search failed: ${text || res.statusText}`),
          ]);
          return;
        }
        const data = await res.json();
        const hits = Array.isArray(data.results?.results)
          ? data.results.results
          : Array.isArray(data.results)
          ? data.results
          : [];

        if (hits.length === 0) {
          setMessages((prev) => [
            ...prev,
            createMessage("system", `No memories matched â€œ${query}â€.`),
          ]);
          return;
        }

        const formatted = hits
          .map((item: any, idx: number) => {
            const memoryText = item?.memory ?? item?.value ?? JSON.stringify(item);
            return `${idx + 1}. ${memoryText}`;
          })
          .join("\n\n");

        setMessages((prev) => [
          ...prev,
          createMessage("system", `ðŸ” Search results for â€œ${query}â€:\n\n${formatted}`),
        ]);
      } catch (error) {
        console.error(error);
        setMessages((prev) => [
          ...prev,
          createMessage("system", "âš ï¸ Could not reach the search API."),
        ]);
      }
      return;
    }

    if (normalized === "all") {
      try {
        const url = new URL(`${API_BASE}/all`);
        const res = await fetch(url.toString());
        if (!res.ok) {
          const text = await res.text();
          setMessages((prev) => [
            ...prev,
            createMessage("system", `âŒ Fetch failed: ${text || res.statusText}`),
          ]);
          return;
        }
        const data = await res.json();
        const memories = Array.isArray(data.memories) ? data.memories : [];
        if (memories.length === 0) {
          setMessages((prev) => [
            ...prev,
            createMessage("system", "No stored memories yet."),
          ]);
          return;
        }
        const formatted = memories
          .map((item: any, idx: number) => {
            const meta = item?.metadata?.type ? ` (${item.metadata.type})` : "";
            const base = item?.memory ?? item?.value ?? JSON.stringify(item);
            return `${idx + 1}. ${base}${meta}`;
          })
          .join("\n\n");
        setMessages((prev) => [
          ...prev,
          createMessage("system", `ðŸ—‚ï¸ Complete memory listing:\n\n${formatted}`),
        ]);
      } catch (error) {
        console.error(error);
        setMessages((prev) => [
          ...prev,
          createMessage("system", "âš ï¸ Could not fetch stored memories."),
        ]);
      }
      return;
    }

    if (normalized === "reset") {
      try {
        const res = await fetch(`${API_BASE}/reset`, { method: "POST" });
        if (!res.ok) {
          const text = await res.text();
          setMessages((prev) => [
            ...prev,
            createMessage("system", `âŒ Reset failed: ${text || res.statusText}`),
          ]);
          return;
        }
        const data = await res.json();
        setMessages((prev) => [
          ...prev,
          createMessage("system", data?.message ?? "Memory reset complete."),
        ]);
      } catch (error) {
        console.error(error);
        setMessages((prev) => [
          ...prev,
          createMessage("system", "âš ï¸ Could not reach the reset endpoint."),
        ]);
      }
      return;
    }

    if (normalized === "help") {
      setMessages((prev) => [
        ...prev,
        createMessage(
          "system",
          [
            "Available commands:",
            "/search <keywords> â€” find stored memories",
            "/all â€” show every memory",
            "/reset â€” clear the vector store",
            "/help â€” show this help",
          ].join("\n")
        ),
      ]);
      return;
    }

    setMessages((prev) => [
      ...prev,
      createMessage("system", `Unknown command â€œ/${normalized}â€. Try /help.`),
    ]);
  };

  const handleNewChat = () => {
    setMessages([
      createMessage(
        "system",
        "New chat started. Say hello to initialise a session and begin capturing context."
      ),
    ]);
    setSessionId(null);
    setInput("");
  };

  return (
    <div className="chat-app">
      <div className="chat-shell">
        <aside className="chat-sidebar">
          <div className="sidebar-header">
            <div>
              <h1 className="sidebar-title">Mem0 Classroom</h1>
              <p className="sidebar-subtitle">Fully local tutor (Ollama + Qdrant)</p>
            </div>
            <button className="ghost-button" onClick={handleNewChat}>
              New chat
            </button>
          </div>

          <div className="sidebar-section">
            <h2>Session</h2>
            {sessionId ? (
              <code className="session-chip">{sessionId}</code>
            ) : (
              <p>No active session yet.</p>
            )}
          </div>

          <div className="sidebar-section">
            <h2>Shortcuts</h2>
            <ul className="command-list">
              <li>
                <span className="command">/search &lt;keywords&gt;</span>
                <span>Look up relevant memories</span>
              </li>
              <li>
                <span className="command">/all</span>
                <span>List every stored memory</span>
              </li>
              <li>
                <span className="command">/reset</span>
                <span>Clear local memory store</span>
              </li>
              <li>
                <span className="command">/help</span>
                <span>Show command reference</span>
              </li>
            </ul>
          </div>
        </aside>

        <main className="chat-main">
          <header className="chat-header">
            <div>
              <h2 className="chat-header-title">AI Student Mentor</h2>
              <p className="chat-header-subtitle">
                Guide the learner and the assistant will respond with grounded context.
              </p>
            </div>
            <span className={`status-badge ${isSending ? "typing" : "idle"}`}>
              {isSending ? "Generatingâ€¦" : "Idle"}
            </span>
          </header>

          <section className="message-list">
            {messages.map((message) => (
              <article
                key={message.id}
                className={`message-row ${message.role}`}
                aria-label={`${ROLE_META[message.role].label} message`}
              >
                <div className={`message-avatar ${message.role}`}>
                  {ROLE_META[message.role].initial}
                </div>
                <div className={`message-bubble ${message.role}`}>
                  <div className="message-meta">
                    <span className="role">{ROLE_META[message.role].label}</span>
                    <span className="time">{formatTime(message.createdAt)}</span>
                  </div>
                  <div className="message-text">{message.text}</div>
                </div>
              </article>
            ))}

            {isSending && (
              <article className="message-row student typing" aria-live="polite">
                <div className="message-avatar student">S</div>
                <div className="message-bubble student">
                  <div className="message-meta">
                    <span className="role">Student</span>
                    <span className="time">â€¦</span>
                  </div>
                  <div className="typing-indicator">
                    <span />
                    <span />
                    <span />
                  </div>
                </div>
              </article>
            )}
            <div ref={bottomRef} />
          </section>

          <form className="chat-input" onSubmit={handleSubmit}>
            <textarea
              className="chat-textarea"
              placeholder="Send a prompt or /command. Press Enter to send, Shift+Enter for a new line."
              value={input}
              disabled={isSending}
              onChange={(event) => setInput(event.target.value)}
              onKeyDown={async (event) => {
                if (event.key === "Enter" && !event.shiftKey) {
                  event.preventDefault();
                  await processInput(input);
                }
              }}
              rows={Math.min(6, input.split("\n").length + 1)}
            />
            <div className="chat-actions">
              <button
                type="button"
                className="secondary-button"
                onClick={() => setInput((prev) => prev + "\n")}
                disabled={isSending}
              >
                New line
              </button>
              <button type="submit" className="primary-button" disabled={isSending}>
                {isSending ? "Sendingâ€¦" : "Send"}
              </button>
            </div>
          </form>
        </main>
      </div>
    </div>
  );
}
