import React, { useState } from "react";

type Message = {
  role: "teacher" | "student";
  text: string;
};

export default function ChatApp() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState<string>("");

  const send = async () => {
    if (!input.trim()) return;

    // handle commands like /topic or /session
    if (input.startsWith("/")) {
      await handleCommand(input);
      setInput("");
      return;
    }

    try {
      const res = await fetch(`http://localhost:8000/chat?prompt=${encodeURIComponent(input)}`);
      const data = await res.json();
      setMessages((prev) => [
        ...prev,
        { role: "teacher", text: input },
        { role: "student", text: data.response },
      ]);
      setInput("");
    } catch (e) {
      console.error("Chat error:", e);
    }
  };

  const handleCommand = async (cmd: string) => {
    if (cmd.startsWith("/session")) {
      const res = await fetch(`http://localhost:8000/session`, { method: "POST" });
      const data = await res.json();
      setMessages((prev) => [...prev, { role: "system", text: data.message } as any]);
    } else if (cmd.startsWith("/topic")) {
      const topic = cmd.split(" ")[1] || "general";
      const res = await fetch(`http://localhost:8000/topic?new_topic=${topic}`, { method: "POST" });
      const data = await res.json();
      setMessages((prev) => [...prev, { role: "system", text: data.message } as any]);
    } else if (cmd.startsWith("/search")) {
      const query = cmd.split(" ").slice(1).join(" ");
      const res = await fetch(`http://localhost:8000/search?query=${encodeURIComponent(query)}`);
      const data = await res.json();
      const results = data.results.results.map((r: any) => `🔍 ${r.memory}`).join("\n");
      setMessages((prev) => [...prev, { role: "system", text: results } as any]);
    } else {
      setMessages((prev) => [...prev, { role: "system", text: "Unknown command." } as any]);
    }
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <div className="h-96 overflow-y-auto border p-3 rounded bg-gray-50">
        {messages.map((m, i) => (
          <div
            key={i}
            className={`my-1 ${
              m.role === "teacher"
                ? "text-blue-600 text-right"
                : m.role === "student"
                ? "text-green-700 text-left"
                : "text-gray-500 text-center"
            }`}
          >
            {m.role !== "system" && <b>{m.role}:</b>} {m.text}
          </div>
        ))}
      </div>
      <div className="flex mt-2">
        <input
          className="flex-1 border p-2 rounded-l"
          value={input}
          placeholder="Type message or /command"
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && send()}
        />
        <button className="bg-blue-500 text-white px-4 rounded-r" onClick={send}>
          Send
        </button>
      </div>
    </div>
  );
}
