import React, { useState } from "react";

export default function ChatApp() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const send = async () => {
    if (!input.trim()) return;
    const res = await fetch(`http://localhost:8000/chat?prompt=${encodeURIComponent(input)}`);
    const data = await res.json();
    setMessages([...messages, { role: "teacher", text: input }, { role: "student", text: data.response }]);
    setInput("");
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <div className="h-96 overflow-y-auto border p-3 rounded">
        {messages.map((m, i) => (
          <div key={i} className={m.role === "teacher" ? "text-blue-600" : "text-green-700"}>
            <b>{m.role}:</b> {m.text}
          </div>
        ))}
      </div>
      <div className="flex mt-2">
        <input className="flex-1 border p-2 rounded-l" value={input} onChange={(e) => setInput(e.target.value)}
               onKeyDown={(e) => e.key === "Enter" && send()} />
        <button className="bg-blue-500 text-white px-4 rounded-r" onClick={send}>Send</button>
      </div>
    </div>
  );
}
